def part1():
    result = inp.count("(") - inp.count(")")
    print("Final floor: {}".format(result))

def part2():
    floor = 0
    for i in range(len(inp)):
        x = inp[i]
        if x == "(":
            floor += 1
        elif x == ")":
            floor -= 1

        if floor < 0:
            print("Reaches basement at position {}".format(i + 1))
            return

    print("Santa never reached the basement")

